import React, { useState, useEffect } from 'react';
import { Table } from 'react-bootstrap';
import axios from 'axios';
import Cookies from 'js-cookie';
import { useLocation } from 'react-router';

function MyReports() {
  const [reports, setReports] = useState([]);
  const location = useLocation();
  const userId = Cookies.get('patientId'); // Assuming userId is stored in the cookie

  useEffect(() => {
    if (userId) {
      // Fetch reports for the logged-in user from the API
      axios.get(`http://localhost:8080/file/${userId}`)
        .then(response => {
          setReports(response.data); // Assuming response.data is an array of reports
        })
        .catch(error => {
          console.error('Error fetching reports:', error);
        });
    }
  }, [userId]); // Fetch reports whenever userId changes

  return (
    <div className="p-4 mt-5">
      <h2>My Reports</h2>
      <Table striped bordered hover>
        <thead>
          <tr>
            <th>Report ID</th>
            <th>File Name</th>
            <th>File Path</th>
          </tr>
        </thead>
        <tbody>
          {reports.map(report => ( // Ensure reports is an array before calling map
            <tr key={report.reportId}>
              <td>{report.reportId}</td>
              <td>{report.fileName}</td>
              <td>{report.filePath}</td>
            </tr>
          ))}
        </tbody>
      </Table>
    </div>
  );
}

export default MyReports;
