import React, { useState, useEffect } from 'react';
import { useCookies } from 'react-cookie';
import './style.css';
import backgroundImage from './background.jpg';

function Appointment() {
  const [appointmentTime, setAppointmentTime] = useState('');
  const [test, setTest] = useState('');
  const [testOptions, setTestOptions] = useState([]);
  const [message, setMessage] = useState('');
  const [cookies] = useCookies(['patientId']); // Retrieve patientId from cookies

  // Fetch test options from the server on component mount
  useEffect(() => {
    const fetchTestOptions = async () => {
      try {
        const response = await fetch('http://localhost:8080/tests'); // Replace with your endpoint
        if (response.ok) {
          const data = await response.json();
          setTestOptions(data);
        } else {
          console.error('Error fetching test options:', response.statusText);
        }
      } catch (error) {
        console.error('Error fetching test options:', error);
      }
    };

    fetchTestOptions();
  }, []);

  // Function to handle form submission
  const handleSubmit = async (event) => {
    event.preventDefault();

    const requestBody = {
      appointmentTime,
      test,
      patient: {
        patientId: cookies.patientId,
      },
    };

    try {
      const response = await fetch('http://localhost:8080/appointments/create', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(requestBody),
      });

      if (response.ok) {
        // Reset form fields
        setAppointmentTime('');
        setTest('');
        setMessage('Appointment request submitted successfully!');
      } else {
        console.error('Error submitting appointment request:', response.statusText);
        setMessage('Error submitting appointment request. Please try again.');
      }
    } catch (error) {
      console.error('Error submitting appointment request:', error);
      setMessage('Error submitting appointment request. Please try again.');
    }
  };

  return (
    <div style={{ backgroundImage: `url(${backgroundImage})`, justifyContent: 'center', alignItems: 'center' }}>
      <h2 style={{ color: '#00264d', textAlign: 'center', marginTop: '10%' }}>Make an Appointment</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="appointmentTime" style={{ color: '#00264d', display: 'block', marginBottom: '8px', textAlign: 'left' }}>Appointment Time:</label>
          <input
            type="datetime-local"
            className="form-control"
            id="appointmentTime"
            value={appointmentTime}
            onChange={(e) => setAppointmentTime(e.target.value)}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="test" style={{ color: '#00264d', display: 'block', marginBottom: '8px', textAlign: 'left' }}>Test:</label>
          <select
            className="form-control"
            id="test"
            value={test}
            onChange={(e) => setTest(e.target.value)}
            required
          >
            <option value="" disabled>Select a test</option>
            {testOptions.map((option) => (
              <option key={option.testId} value={option.testName}>
                {option.testName}
              </option>
            ))}
          </select>
        </div>
        <button type="submit" className="btn btn-primary">Submit</button>
      </form>
      {message && <div className="alert alert-success mt-3">{message}</div>}
    </div>
  );
}

export default Appointment;
