import React, { useState, useEffect } from 'react';
import { Button, Table, Modal, Form } from 'react-bootstrap';
import axios from 'axios';

function UpdateModal({ show, handleClose, testResult, handleUpdate }) {
  const [updatedTestName, setUpdatedTestName] = useState(testResult.testName);
  const [updatedResult, setUpdatedResult] = useState(testResult.result);

  const handleUpdateClick = () => {
    handleUpdate(testResult.testResultId, { testName: updatedTestName, result: updatedResult });
    handleClose();
  };

  return (
    <Modal show={show} onHide={handleClose}>
      <Modal.Header closeButton>
        <Modal.Title>Update Test Result</Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <Form>
          <Form.Group controlId="formTestName">
            <Form.Label>Test Name</Form.Label>
            <Form.Control
              type="text"
              value={updatedTestName}
              onChange={(e) => setUpdatedTestName(e.target.value)}
            />
          </Form.Group>
          <Form.Group controlId="formResult">
            <Form.Label>Result</Form.Label>
            <Form.Control
              type="text"
              value={updatedResult}
              onChange={(e) => setUpdatedResult(e.target.value)}
            />
          </Form.Group>
        </Form>
      </Modal.Body>
      <Modal.Footer>
        <Button variant="secondary" onClick={handleClose}>
          Close
        </Button>
        <Button variant="primary" onClick={handleUpdateClick}>
          Update
        </Button>
      </Modal.Footer>
    </Modal>
  );
}

function AllTestResults() {
  const [testResults, setTestResults] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [showModal, setShowModal] = useState(false);
  const [selectedTestResult, setSelectedTestResult] = useState(null);
  const location = useLocation();
  const roleid = location.state;

  useEffect(() => {
    axios.get('http://localhost:8080/api/test-results/all')
      .then(response => {
        setTestResults(response.data);
      })
      .catch(error => {
        console.error('Error fetching test results:', error);
      });
  }, []);

  const handleSearch = (event) => {
    setSearchTerm(event.target.value);
  };

  const handleShowModal = (testResult) => {
    setSelectedTestResult(testResult);
    setShowModal(true);
  };

  const handleCloseModal = () => {
    setShowModal(false);
  };

  const handleUpdate = (testResultId, newData) => {
    axios.put(`http://localhost:8080/api/test-results/test-results/${testResultId}`, newData)
      .then(response => {
        console.log('Test result updated successfully:', response.data);
        // Update local state or refetch data as needed
      })
      .catch(error => {
        console.error('Error updating test result:', error);
      });
  };

  return (
    <div className="d-flex">
      <div>
        <Sidebar />
      </div>
      <div className="flex-grow-1">
        <Topbar roleid={roleid} />
        <div className="p-4 mt-5">
          <h2>All Test Results</h2>
          <div className="mb-3">
            <input
              type="text"
              placeholder="Search..."
              value={searchTerm}
              onChange={handleSearch}
              className="form-control"
            />
          </div>
          <Table striped bordered hover>
            <thead>
              <tr>
                <th>Test Name</th>
                <th>Result</th>
                <th>Patient Name</th>
                <th>Contact Number</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {testResults
                .filter(testResult => testResult.testName.toLowerCase().includes(searchTerm.toLowerCase()))
                .map(testResult => (
                  <tr key={testResult.testResultId}>
                    <td>{testResult.testName}</td>
                    <td>{testResult.result}</td>
                    <td>{testResult.patient.firstName} {testResult.patient.lastName}</td>
                    <td>{testResult.patient.contactNumber}</td>
                    <td>
                      <Button variant="primary" onClick={() => handleShowModal(testResult)}>Update</Button>
                      <Button variant="danger" onClick={() => handleDelete(testResult.testResultId)}>Delete</Button>
                    </td>
                  </tr>
                ))}
              {testResults.length === 0 && (
                <tr>
                  <td colSpan="5">No test results available</td>
                </tr>
              )}
            </tbody>
          </Table>
        </div>
      </div>
      <UpdateModal
        show={showModal}
        handleClose={handleCloseModal}
        testResult={selectedTestResult}
        handleUpdate={handleUpdate}
      />
    </div>
  );
}

export default AllTestResults;
