import React, { useState, useEffect } from 'react';
import Topbar from './Topbar';
import Sidebar from './Sidebar';
import { Table } from 'react-bootstrap';
import axios from 'axios';
import { useLocation } from 'react-router';

function Appointments() {
  const [appointments, setAppointments] = useState([]);
  const [clickedButton, setClickedButton] = useState(null);
  const location = useLocation();
  const roleid = location.state;

  useEffect(() => {
    // Fetch appointments from the API
    axios.get('http://localhost:8080/appointments/all')
      .then(response => {
        setAppointments(response.data);
      })
      .catch(error => {
        console.error('Error fetching appointments:', error);
      });
  }, []);

  const handleStatusChange = async (appointmentId, newStatus) => {
    try {
      // Make the API request using axios to update the appointment status
      await axios.put(`http://localhost:8080/appointments/update-status/${appointmentId}`, { newStatus });

      // Update the local state to reflect the status change
      setAppointments(prevAppointments =>
        prevAppointments.map(appointment =>
          appointment.appointmentId === appointmentId ? { ...appointment, status: newStatus } : appointment
        )
      );

      // Set the clicked button to highlight it
      setClickedButton(appointmentId);
    } catch (error) {
      console.error('Error updating appointment status:', error);
    }
  };

  return (
    <div className="d-flex">
      <div>
        <Sidebar />
      </div>
      <div className="flex-grow-1">
        <Topbar roleid={roleid} />
        <div className="p-4 mt-5">
          <h2>Appointments</h2>
          <Table striped bordered hover>
            <thead>
              <tr>
                <th>Appointment Number</th>
                <th>Appointment Time</th>
                <th>Test</th>
                <th>Status</th>
                <th>Patient Name</th>
                <th>Contact Number</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {appointments.map(appointment => (
                <tr key={appointment.appointmentId}>
                  <td>{appointment.appointmentNumber}</td>
                  <td>{new Date(appointment.appointmentTime).toLocaleString()}</td>
                  <td>{appointment.test}</td>
                  <td>{appointment.status}</td>
                  <td>{`${appointment.patient.firstName} ${appointment.patient.lastName}`}</td>
                  <td>{appointment.patient.contactNumber}</td>
                  <td>
                    {/* Action buttons for changing status */}
                    <button
                      className={`btn ${clickedButton === appointment.appointmentId ? 'btn-success' : 'btn-outline-success'}`}
                      onClick={() => handleStatusChange(appointment.appointmentId, 'Confirmed')}
                      disabled={appointment.status === 'Confirmed'}
                    >
                      Confirm
                    </button>
                    <button
                      className={`btn btn-link ${clickedButton === appointment.appointmentId ? 'text-danger' : 'text-danger'}`}
                      onClick={() => handleStatusChange(appointment.appointmentId, 'Cancelled')}
                      disabled={appointment.status === 'Cancelled'}
                    >
                      Cancel
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </Table>
        </div>
      </div>
    </div>
  );
}

export default Appointments;
